/*
  Proyecto: Programación Móvil
  Estudiantes:
    - Alejandro Arce
    - Brenda Bravo

  Este archivo implementa la pantalla principal (HomeScreen) de PumApp,
  con un carousel de noticias UNAM, tips de la semana, enlaces de interés,
  saludo personalizado, mensaje de apoyo y navegación inferior.

  A continuación se explica cada sección y función en detalle.
*/

import 'dart:async';
import 'dart:io';
import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:url_launcher/url_launcher.dart';

import 'login_screen.dart';
import 'calendar_screen.dart';
import 'recursos_screen.dart';
import 'comunidad_screen.dart';
import 'profile_screen.dart';

/// Representa una noticia con título, imagen y enlace.
class NewsItem {
  final String title;
  final String imageUrl;
  final String url;
  NewsItem({
    required this.title,
    required this.imageUrl,
    required this.url,
  });
}

/// Un enlace de interés (SIAE, Biblioteca, etc).
class LinkItem {
  final String title;
  final String url;
  LinkItem({required this.title, required this.url});
}

class HomeScreen extends StatefulWidget {
  const HomeScreen({Key? key}) : super(key: key);
  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  int _selectedIndex = 0;
  String _userName = "Estudiante";
  String? _imagePath;
  late List<NewsItem> _news;      // Lista fija de noticias simuladas UNAM
  int _newsIndex = 0;              // Índice actual del carousel

  // Cinco tips para rotar cada semana
  final List<String> _tips = [
    "Organiza tus materias desde el inicio del semestre 📅",
    "Apóyate en tus profesores y compañeros 🤝",
    "Consulta las bibliotecas digitales de la UNAM 📚",
    "No olvides respaldar tus apuntes en la nube ☁️",
    "Cuida tu salud mental, date un respiro 🧠"
  ];

  // Enlaces oficiales de la UNAM
  final List<LinkItem> _links = [
    LinkItem(title: "SIAE", url: "https://www.dgae-siae.unam.mx"),
    LinkItem(title: "Biblioteca Virtual", url: "https://bidi.unam.mx"),
    LinkItem(title: "Calendario Escolar", url: "https://www.dgae-siae.unam.mx/actividades/calendarios/2025-semestral.pdf"),
    LinkItem(title: "Comunidad UNAM", url: "https://www.comunidad.unam.mx/"),
  ];

  @override
  void initState() {
    super.initState();

    // 1) Carga perfil de SharedPreferences
    _loadUserProfile();

    // 2) Inicializa lista de noticias con enlaces e imágenes reales
    _news = [
      NewsItem(
        title: "UNAM inaugura nueva biblioteca digital",
        imageUrl: "https://th.bing.com/th/id/OIP.jsDYUyUnass3I6x6qqj3CgHaDX?w=310&h=159&c=7&r=0&o=7&cb=iwp2&dpr=1.3&pid=1.7&rm=3",
        url: "https://elpopular.mx/comunidad/2025/04/23/biblioteca-digital-de-la-unam-llega-a-tehuacan-nuestros-estudiantes-podran-acceder-a-mas-de-451-mil-titulos",
      ),
      NewsItem(
        title: "Convocatoria Becas UNAM 2025",
        imageUrl: "https://th.bing.com/th/id/OIP.mJKUF4uTLR4zefwTDEd35wHaDo?cb=iwp2&rs=1&pid=ImgDetMain",
        url: "https://www.becas.unam.mx/",
      ),
      NewsItem(
        title: "Investigación en Ciencias de la Tierra",
        imageUrl: "https://th.bing.com/th/id/OIP.nWFONiaYLW8IH81COHkFlAHaEP?w=257&h=180&c=7&r=0&o=5&cb=iwc2&dpr=1.3&pid=1.7",
        url: "https://www.gaceta.unam.mx/tag/direccion-de-la-escuela-nacional-de-ciencias-de-la-tierra/",
      ),
      NewsItem(
        title: "Festival Universitario 2025",
        imageUrl: "data:image/jpeg;base64,",
        url: "https://teatrounam.com.mx/teatro/entradasteatro/convocatoria-2024-31o-festival-internacional-de-teatro-universitario/",
      ),
    ];

    // 3) Configura timer para rotar noticias cada 5 segundos
    Timer.periodic(const Duration(seconds: 5), (_) {
      setState(() {
        _newsIndex = (_newsIndex + 1) % _news.length;
      });
    });
  }

  /// Carga nombre e imagen de perfil guardados en SharedPreferences.
  Future<void> _loadUserProfile() async {
    final prefs = await SharedPreferences.getInstance();
    setState(() {
      _userName    = prefs.getString('name') ?? 'Estudiante';
      _imagePath   = prefs.getString('profile_image');
    });
  }

  /// Cierra sesión en Firebase Auth y vuelve al LoginScreen.
  Future<void> _logout() async {
    await FirebaseAuth.instance.signOut();
    if (!mounted) return;
    Navigator.pushReplacement(
      context,
      MaterialPageRoute(builder: (_) => const LoginScreen()),
    );
  }

  /// Construye la pestaña "Inicio" con saludo, mensaje, tips, noticias y enlaces.
  Widget _buildInicio() {
    // Selecciona el tip basado en el número de la semana del año
    final tipOfWeek = _tips[DateTime.now().weekOfYear % _tips.length];
    final currentNews = _news[_newsIndex];

    return SingleChildScrollView(
      padding: const EdgeInsets.all(24),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [

          // 1) Saludo + botón de logout
          Row(
            children: [
              CircleAvatar(
                radius: 28,
                backgroundImage:
                _imagePath != null ? FileImage(File(_imagePath!)) : null,
                child: _imagePath == null
                    ? const Icon(Icons.person, size: 30, color: Colors.black)
                    : null,
              ),
              const SizedBox(width: 12),
              Expanded(
                child: Text(
                  '¡Hola, $_userName! 👋',
                  style: const TextStyle(
                    fontSize: 26,
                    fontWeight: FontWeight.bold,
                    color: Colors.white,
                  ),
                ),
              ),
              IconButton(
                onPressed: _logout,
                icon: const Icon(Icons.logout),
                color: Colors.redAccent,
                tooltip: 'Cerrar sesión',
              ),
            ],
          ),

          const SizedBox(height: 32),

          // 2) Mensaje de apoyo
          Container(
            width: double.infinity,
            padding: const EdgeInsets.all(16),
            decoration: BoxDecoration(
              color: Colors.white10,
              borderRadius: BorderRadius.circular(12),
            ),
            child: const Text(
              'Recuerda que cada paso te acerca a tus sueños.\n¡PumApp está contigo! 🐾',
              style: TextStyle(color: Colors.white70, fontSize: 16),
              textAlign: TextAlign.center,
            ),
          ),

          const SizedBox(height: 32),

          // 3) Tip de la semana
          const Text(
            'Tip de la semana 🧠',
            style: TextStyle(
              fontSize: 20, fontWeight: FontWeight.w600, color: Color(0xFFCCA242),
            ),
          ),
          const SizedBox(height: 12),
          Row(
            children: [
              const Icon(Icons.lightbulb, size: 32, color: Color(0xFFCCA242)),
              const SizedBox(width: 12),
              Expanded(
                child: Text(
                  tipOfWeek,
                  style: const TextStyle(fontSize: 16, color: Colors.white70),
                ),
              ),
            ],
          ),

          const SizedBox(height: 32),

          // 4) Noticias UNAM
          const Text(
            'Noticias UNAM 📰',
            style: TextStyle(
              fontSize: 20, fontWeight: FontWeight.w600, color: Color(0xFFCCA242),
            ),
          ),
          const SizedBox(height: 12),
          SizedBox(
            height: 180,
            child: GestureDetector(
              onTap: () => launchUrl(Uri.parse(currentNews.url)),
              child: ClipRRect(
                borderRadius: BorderRadius.circular(12),
                child: Stack(
                  fit: StackFit.expand,
                  children: [
                    // Imagen de la noticia
                    Image.network(
                      currentNews.imageUrl,
                      fit: BoxFit.cover,
                      errorBuilder: (_, __, ___) =>
                      const Center(child: Icon(Icons.broken_image, color: Colors.white30)),
                    ),
                    // Título sobre degradado
                    Container(
                      alignment: Alignment.bottomLeft,
                      padding: const EdgeInsets.all(12),
                      decoration: BoxDecoration(
                        gradient: LinearGradient(
                          colors: [Colors.transparent, Colors.black54],
                          begin: Alignment.topCenter,
                          end: Alignment.bottomCenter,
                        ),
                      ),
                      child: Text(
                        currentNews.title,
                        maxLines: 2,
                        overflow: TextOverflow.ellipsis,
                        style: const TextStyle(color: Colors.white, fontSize: 16),
                      ),
                    )
                  ],
                ),
              ),
            ),
          ),

          const SizedBox(height: 32),

          // 5) Enlaces UNAM
          const Text(
            'Enlaces de interés 🔗',
            style: TextStyle(
              fontSize: 20, fontWeight: FontWeight.w600, color: Color(0xFFCCA242),
            ),
          ),
          const SizedBox(height: 12),
          SizedBox(
            height: 80,
            child: ListView.separated(
              scrollDirection: Axis.horizontal,
              itemCount: _links.length,
              separatorBuilder: (_, __) => const SizedBox(width: 12),
              itemBuilder: (_, i) {
                final link = _links[i];
                return ElevatedButton(
                  onPressed: () => launchUrl(Uri.parse(link.url)),
                  style: ElevatedButton.styleFrom(
                    backgroundColor: Colors.white10,
                    foregroundColor: Colors.white,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(12),
                    ),
                  ),
                  child: Text(link.title),
                );
              },
            ),
          ),

          const SizedBox(height: 40),
        ],
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    // Configura las pestañas de navegación inferior
    final screens = <Widget>[
      _buildInicio(),
      const CalendarScreen(),
      const RecursosScreen(),
      const ComunidadScreen(),
      const ProfileScreen(),
    ];

    return Scaffold(
      backgroundColor: const Color(0xFF0A243C),
      body: screens[_selectedIndex],
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: _selectedIndex,
        backgroundColor: const Color(0xFF0A243C),
        selectedItemColor: const Color(0xFFCCA242),
        unselectedItemColor: Colors.white54,
        onTap: (idx) => setState(() => _selectedIndex = idx),
        type: BottomNavigationBarType.fixed,
        items: const [
          BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Inicio'),
          BottomNavigationBarItem(icon: Icon(Icons.calendar_today), label: 'Calendario'),
          BottomNavigationBarItem(icon: Icon(Icons.folder_copy), label: 'Recursos'),
          BottomNavigationBarItem(icon: Icon(Icons.groups), label: 'Comunidad'),
          BottomNavigationBarItem(icon: Icon(Icons.person), label: 'Perfil'),
        ],
      ),
    );
  }
}

/// Extensión auxiliar para obtener el número de semana del año.
extension on DateTime {
  int get weekOfYear {
    final first = DateTime(year, 1, 1);
    return ((difference(first).inDays + first.weekday) / 7).ceil();
  }
}
